import React, { useState, useEffect, useCallback } from 'react';

// Main App Component
export default function App() {
    // State Management
    const [page, setPage] = useState('login'); // login, register, user_dashboard, admin_dashboard, quiz, add_question, create_quiz
    const [token, setToken] = useState(null);
    const [user, setUser] = useState(null);
    const [selectedQuizId, setSelectedQuizId] = useState(null);
    const [message, setMessage] = useState({ text: '', type: '' }); // type can be 'success' or 'error'

    // --- API Configuration ---
    // IMPORTANT: Change this to your Spring Boot backend URL if it's different.
    const API_BASE_URL = 'http://localhost:8080';

    // --- Helper Functions ---
    const showMessage = (text, type) => {
        setMessage({ text, type });
        setTimeout(() => setMessage({ text: '', type: '' }), 5000);
    };

    const parseJwt = (token) => {
        try {
            return JSON.parse(atob(token.split('.')[1]));
        } catch (e) {
            return null;
        }
    };

    // --- Authentication ---
    useEffect(() => {
        const storedToken = localStorage.getItem('quiz_app_token');
        if (storedToken) {
            const decodedToken = parseJwt(storedToken);
            if (decodedToken && decodedToken.exp * 1000 > Date.now()) {
                setToken(storedToken);
                setUser({
                    username: decodedToken.sub,
                    roles: decodedToken.roles || []
                });
            } else {
                localStorage.removeItem('quiz_app_token');
            }
        }
    }, []);

    useEffect(() => {
        if (user) {
            if (user.roles.includes('ROLE_ADMIN')) {
                setPage('admin_dashboard');
            } else {
                setPage('user_dashboard');
            }
        } else {
            setPage('login');
        }
    }, [user]);

    const handleLogin = async (username, password) => {
        try {
            const response = await fetch(`${API_BASE_URL}/auth/login`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });
            if (!response.ok) {
                throw new Error('Login failed. Please check your credentials.');
            }
            const data = await response.json();
            const decodedToken = parseJwt(data.token);
            localStorage.setItem('quiz_app_token', data.token);
            setToken(data.token);
            setUser({
                username: decodedToken.sub,
                roles: decodedToken.roles || []
            });
            showMessage('Login successful!', 'success');
        } catch (error) {
            console.error('Login error:', error);
            showMessage(error.message, 'error');
        }
    };

    const handleRegister = async (username, password, email) => {
        try {
            const response = await fetch(`${API_BASE_URL}/auth/register`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password, email }),
            });
            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText || 'Registration failed.');
            }
            showMessage('Registration successful! Please log in.', 'success');
            setPage('login');
        } catch (error) {
            console.error('Registration error:', error);
            showMessage(error.message, 'error');
        }
    };

    const handleLogout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('quiz_app_token');
        setPage('login');
        showMessage('You have been logged out.', 'success');
    };

    // --- Navigation ---
    const navigateTo = (newPage) => {
        setPage(newPage);
    };

    const startQuiz = (quizId) => {
        setSelectedQuizId(quizId);
        setPage('quiz');
    };

    // --- Component Rendering Logic ---
    const renderPage = () => {
        switch (page) {
            case 'register':
                return <Register onRegister={handleRegister} navigateTo={navigateTo} />;
            case 'user_dashboard':
                return <UserDashboard token={token} startQuiz={startQuiz} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'admin_dashboard':
                return <AdminDashboard token={token} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'quiz':
                return <Quiz token={token} quizId={selectedQuizId} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'add_question':
                return <AddQuestion token={token} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'create_quiz':
                return <CreateQuiz token={token} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'login':
            default:
                return <Login onLogin={handleLogin} navigateTo={navigateTo} />;
        }
    };

    return (
        <div className="bg-gray-100 min-h-screen font-sans">
            <header className="bg-white shadow-md">
                <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
                    <div className="text-2xl font-bold text-gray-800">
                        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="inline-block mr-2 text-indigo-600"><path d="M14 9a2 2 0 0 1-2 2H6l-4 4V4c0-1.1.9-2 2-2h8a2 2 0 0 1 2 2v5Z"/><path d="M18 9h2a2 2 0 0 1 2 2v9l-4-4h-2a2 2 0 0 1-2-2V11a2 2 0 0 1 2-2Z"/></svg>
                        QuizMaster Pro
                    </div>
                    {user && (
                        <div className="flex items-center space-x-4">
                            <span className="text-gray-600">Welcome, <span className="font-semibold text-indigo-600">{user.username}</span></span>
                            <button onClick={handleLogout} className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition duration-300">
                                Logout
                            </button>
                        </div>
                    )}
                </nav>
            </header>
            <main className="container mx-auto p-6">
                {message.text && <Message text={message.text} type={message.type} />}
                {renderPage()}
            </main>
        </div>
    );
}

// --- Reusable Components ---
const Card = ({ children, className = '' }) => (
    <div className={`bg-white rounded-xl shadow-lg p-6 md:p-8 ${className}`}>
        {children}
    </div>
);

const Button = ({ children, onClick, className = '', type = 'button' }) => (
    <button
        type={type}
        onClick={onClick}
        className={`w-full px-4 py-3 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-300 ${className}`}
    >
        {children}
    </button>
);

const Input = ({ id, type, placeholder, value, onChange, required = false }) => (
    <input
        id={id}
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        required={required}
        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition"
    />
);

const Message = ({ text, type }) => {
    const baseClasses = "p-4 rounded-lg mb-6 text-center";
    const typeClasses = type === 'success'
        ? "bg-green-100 border border-green-400 text-green-700"
        : "bg-red-100 border border-red-400 text-red-700";
    return (
        <div className={`${baseClasses} ${typeClasses}`} role="alert">
            <p className="font-bold">{type === 'success' ? 'Success' : 'Error'}</p>
            <p>{text}</p>
        </div>
    );
};

const Spinner = () => (
    <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-500"></div>
    </div>
);


// --- Page Components ---

// Login Page
function Login({ onLogin, navigateTo }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onLogin(username, password);
    };

    return (
        <div className="max-w-md mx-auto mt-10">
            <Card>
                <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Login</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">Username</label>
                        <Input id="username" type="text" placeholder="Enter your username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    </div>
                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                        <Input id="password" type="password" placeholder="Enter your password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </div>
                    <Button type="submit">Login</Button>
                </form>
                <p className="text-center text-gray-600 mt-6">
                    Don't have an account?{' '}
                    <button onClick={() => navigateTo('register')} className="font-semibold text-indigo-600 hover:text-indigo-500">
                        Register here
                    </button>
                </p>
            </Card>
        </div>
    );
}

// Registration Page
function Register({ onRegister, navigateTo }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onRegister(username, password, email);
    };

    return (
        <div className="max-w-md mx-auto mt-10">
            <Card>
                <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Create an Account</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="username-reg" className="block text-sm font-medium text-gray-700 mb-1">Username</label>
                        <Input id="username-reg" type="text" placeholder="Choose a username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    </div>
                    <div>
                        <label htmlFor="email-reg" className="block text-sm font-medium text-gray-700 mb-1">Email (Optional)</label>
                        <Input id="email-reg" type="email" placeholder="Enter your email" value={email} onChange={(e) => setEmail(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor="password-reg" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                        <Input id="password-reg" type="password" placeholder="Create a password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </div>
                    <Button type="submit">Register</Button>
                </form>
                <p className="text-center text-gray-600 mt-6">
                    Already have an account?{' '}
                    <button onClick={() => navigateTo('login')} className="font-semibold text-indigo-600 hover:text-indigo-500">
                        Login here
                    </button>
                </p>
            </Card>
        </div>
    );
}

// User Dashboard
function UserDashboard({ token, startQuiz, API_BASE_URL, showMessage }) {
    const [quizzes, setQuizzes] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const fetchQuizzes = useCallback(async () => {
        setIsLoading(true);
        try {
            const response = await fetch(`${API_BASE_URL}/admin/quiz/all`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!response.ok) throw new Error('Failed to fetch quizzes.');
            const data = await response.json();
            setQuizzes(data);
        } catch (error) {
            console.error('Fetch quizzes error:', error);
            showMessage(error.message, 'error');
        } finally {
            setIsLoading(false);
        }
    }, [token, API_BASE_URL, showMessage]);

    useEffect(() => {
        fetchQuizzes();
    }, [fetchQuizzes]);

    if (isLoading) return <Spinner />;

    return (
        <Card>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Available Quizzes</h2>
            {quizzes.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {quizzes.map(quiz => (
                        <div key={quiz.id} className="bg-gray-50 p-6 rounded-lg border border-gray-200 flex flex-col justify-between">
                            <div>
                                <h3 className="text-xl font-semibold text-gray-700">{quiz.title}</h3>
                                <p className="text-gray-500 mt-2">{quiz.questions.length} Questions</p>
                            </div>
                            <button onClick={() => startQuiz(quiz.id)} className="mt-4 w-full px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 transition duration-300">
                                Start Quiz
                            </button>
                        </div>
                    ))}
                </div>
            ) : (
                <p className="text-center text-gray-500 py-10">No quizzes are available at the moment. Please check back later.</p>
            )}
        </Card>
    );
}

// Admin Dashboard
function AdminDashboard({ token, navigateTo, API_BASE_URL, showMessage }) {
    const [questions, setQuestions] = useState([]);
    const [quizzes, setQuizzes] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const fetchData = useCallback(async () => {
        setIsLoading(true);
        try {
            // Fetch questions
            const qResponse = await fetch(`${API_BASE_URL}/admin/question/allQuestions`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!qResponse.ok) throw new Error('Failed to fetch questions.');
            const qData = await qResponse.json();
            setQuestions(qData);

            // Fetch quizzes
            const quizResponse = await fetch(`${API_BASE_URL}/admin/quiz/all`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!quizResponse.ok) throw new Error('Failed to fetch quizzes.');
            const quizData = await quizResponse.json();
            setQuizzes(quizData);
        } catch (error) {
            console.error('Fetch admin data error:', error);
            showMessage(error.message, 'error');
        } finally {
            setIsLoading(false);
        }
    }, [token, API_BASE_URL, showMessage]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const deleteQuiz = async (id) => {
        if (window.confirm('Are you sure you want to delete this quiz?')) {
            try {
                const response = await fetch(`${API_BASE_URL}/admin/quiz/delete/${id}`, {
                    method: 'DELETE',
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) throw new Error('Failed to delete quiz.');
                showMessage('Quiz deleted successfully.', 'success');
                fetchData(); // Refresh data
            } catch (error) {
                console.error('Delete quiz error:', error);
                showMessage(error.message, 'error');
            }
        }
    };

    const deleteAllQuizzes = async () => {
        if (window.confirm('Are you sure you want to delete ALL quizzes? This action cannot be undone.')) {
            try {
                const response = await fetch(`${API_BASE_URL}/admin/quiz/delete/all`, {
                    method: 'DELETE',
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) throw new Error('Failed to delete all quizzes.');
                showMessage('All quizzes deleted successfully.', 'success');
                fetchData(); // Refresh data
            } catch (error) {
                console.error('Delete all quizzes error:', error);
                showMessage(error.message, 'error');
            }
        }
    };

    if (isLoading) return <Spinner />;

    return (
        <div className="space-y-8">
            <Card>
                <div className="flex justify-between items-center mb-6">
                    <h2 className="text-3xl font-bold text-gray-800">Admin Dashboard</h2>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <button onClick={() => navigateTo('add_question')} className="w-full text-lg p-6 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 transition duration-300">
                        Add New Question
                    </button>
                    <button onClick={() => navigateTo('create_quiz')} className="w-full text-lg p-6 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 transition duration-300">
                        Create New Quiz
                    </button>
                </div>
            </Card>

            <Card>
                <h3 className="text-2xl font-bold text-gray-800 mb-4">Manage Quizzes ({quizzes.length})</h3>
                {quizzes.length > 0 ? (
                    <>
                        <div className="space-y-4">
                            {quizzes.map(quiz => (
                                <div key={quiz.id} className="bg-gray-50 p-4 rounded-lg flex justify-between items-center border">
                                    <div>
                                        <p className="font-semibold text-gray-700">{quiz.title}</p>
                                        <p className="text-sm text-gray-500">{quiz.questions.length} questions</p>
                                    </div>
                                    <button onClick={() => deleteQuiz(quiz.id)} className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600">Delete</button>
                                </div>
                            ))}
                        </div>
                        <button onClick={deleteAllQuizzes} className="mt-6 w-full px-4 py-2 bg-red-600 text-white font-semibold rounded-lg hover:bg-red-700">
                            Delete All Quizzes
                        </button>
                    </>
                ) : <p className="text-gray-500">No quizzes created yet.</p>}
            </Card>

            <Card>
                <h3 className="text-2xl font-bold text-gray-800 mb-4">All Questions ({questions.length})</h3>
                <div className="max-h-96 overflow-y-auto space-y-3 pr-2">
                    {questions.map(q => (
                        <div key={q.id} className="bg-gray-50 p-3 rounded-lg border">
                            <p className="font-semibold text-gray-700">{q.id}: {q.question_title}</p>
                            <p className="text-sm text-gray-500">Category: {q.category} | Difficulty: {q.difficultylevel}</p>
                        </div>
                    ))}
                </div>
            </Card>
        </div>
    );
}

// Quiz Taking Page
function Quiz({ token, quizId, navigateTo, API_BASE_URL, showMessage }) {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [answers, setAnswers] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [result, setResult] = useState(null);

    useEffect(() => {
        const fetchQuizQuestions = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/user/quiz/get/${quizId}`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) throw new Error('Failed to load quiz questions.');
                const data = await response.json();
                setQuestions(data);
            } catch (error) {
                console.error('Fetch quiz questions error:', error);
                showMessage(error.message, 'error');
            } finally {
                setIsLoading(false);
            }
        };
        fetchQuizQuestions();
    }, [quizId, token, API_BASE_URL, showMessage]);

    const handleAnswerSelect = (questionId, selectedOption) => {
        setAnswers({ ...answers, [questionId]: selectedOption });
    };

    const handleSubmit = async () => {
        const formattedResponses = questions.map(q => ({
            id: q.id,
            response: answers[q.id] || ''
        }));

        try {
            const response = await fetch(`${API_BASE_URL}/user/quiz/submit/${quizId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(formattedResponses)
            });
            if (!response.ok) throw new Error('Failed to submit quiz.');
            const score = await response.json();
            setResult(score);
            showMessage('Quiz submitted successfully!', 'success');
        } catch (error) {
            console.error('Submit quiz error:', error);
            showMessage(error.message, 'error');
        }
    };

    if (isLoading) return <Spinner />;
    if (result !== null) {
        return (
            <Card className="text-center">
                <h2 className="text-3xl font-bold text-gray-800 mb-4">Quiz Complete!</h2>
                <p className="text-xl text-gray-600 mb-6">Your score is:</p>
                <p className="text-6xl font-bold text-indigo-600 mb-8">{result} / {questions.length}</p>
                <Button onClick={() => navigateTo('user_dashboard')}>Back to Dashboard</Button>
            </Card>
        );
    }

    const currentQuestion = questions[currentQuestionIndex];
    if (!currentQuestion) return <p>No questions found for this quiz.</p>;

    const options = [currentQuestion.option1, currentQuestion.option2, currentQuestion.option3, currentQuestion.option4];

    return (
        <Card>
            <div className="mb-6">
                <p className="text-sm text-gray-500">Question {currentQuestionIndex + 1} of {questions.length}</p>
                <h2 className="text-2xl font-semibold text-gray-800 mt-2">{currentQuestion.question_title}</h2>
            </div>
            <div className="space-y-4">
                {options.map((option, index) => (
                    <button
                        key={index}
                        onClick={() => handleAnswerSelect(currentQuestion.id, option)}
                        className={`w-full text-left p-4 rounded-lg border-2 transition duration-200 ${answers[currentQuestion.id] === option ? 'bg-indigo-100 border-indigo-500' : 'bg-white border-gray-300 hover:border-indigo-400'}`}
                    >
                        {option}
                    </button>
                ))}
            </div>
            <div className="mt-8 flex justify-between items-center">
                <button
                    onClick={() => setCurrentQuestionIndex(i => i - 1)}
                    disabled={currentQuestionIndex === 0}
                    className="px-6 py-2 bg-gray-300 text-gray-700 rounded-lg disabled:opacity-50 hover:bg-gray-400"
                >
                    Previous
                </button>
                {currentQuestionIndex === questions.length - 1 ? (
                    <button onClick={handleSubmit} className="px-6 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600">
                        Submit Quiz
                    </button>
                ) : (
                    <button
                        onClick={() => setCurrentQuestionIndex(i => i + 1)}
                        disabled={currentQuestionIndex === questions.length - 1}
                        className="px-6 py-2 bg-indigo-600 text-white rounded-lg disabled:opacity-50 hover:bg-indigo-700"
                    >
                        Next
                    </button>
                )}
            </div>
        </Card>
    );
}

// Add Question Form (Admin)
function AddQuestion({ token, navigateTo, API_BASE_URL, showMessage }) {
    const [formData, setFormData] = useState({
        question_title: '',
        option1: '',
        option2: '',
        option3: '',
        option4: '',
        right_answer: '',
        difficultylevel: '',
        category: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.id]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${API_BASE_URL}/admin/question/addQuestions`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });
            if (!response.ok) throw new Error('Failed to add question.');
            showMessage('Question added successfully!', 'success');
            navigateTo('admin_dashboard');
        } catch (error) {
            console.error('Add question error:', error);
            showMessage(error.message, 'error');
        }
    };

    return (
        <Card>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Add New Question</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input id="question_title" type="text" placeholder="Question Title" value={formData.question_title} onChange={handleChange} required />
                <Input id="option1" type="text" placeholder="Option 1" value={formData.option1} onChange={handleChange} required />
                <Input id="option2" type="text" placeholder="Option 2" value={formData.option2} onChange={handleChange} required />
                <Input id="option3" type="text" placeholder="Option 3" value={formData.option3} onChange={handleChange} required />
                <Input id="option4" type="text" placeholder="Option 4" value={formData.option4} onChange={handleChange} required />
                <Input id="right_answer" type="text" placeholder="Right Answer" value={formData.right_answer} onChange={handleChange} required />
                <Input id="difficultylevel" type="text" placeholder="Difficulty Level" value={formData.difficultylevel} onChange={handleChange} required />
                <Input id="category" type="text" placeholder="Category" value={formData.category} onChange={handleChange} required />
                <div className="flex space-x-4 pt-4">
                    <Button type="button" onClick={() => navigateTo('admin_dashboard')} className="bg-gray-500 hover:bg-gray-600">Cancel</Button>
                    <Button type="submit">Add Question</Button>
                </div>
            </form>
        </Card>
    );
}

// Create Quiz Form (Admin)
function CreateQuiz({ token, navigateTo, API_BASE_URL, showMessage }) {
    const [category, setCategory] = useState('');
    const [numQ, setNumQ] = useState('');
    const [title, setTitle] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const params = new URLSearchParams({ category, numQ, title });
        try {
            const response = await fetch(`${API_BASE_URL}/admin/quiz/create?${params.toString()}`, {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!response.ok) {
                 const errorText = await response.text();
                 throw new Error(errorText || 'Failed to create quiz.');
            }
            showMessage('Quiz created successfully!', 'success');
            navigateTo('admin_dashboard');
        } catch (error) {
            console.error('Create quiz error:', error);
            showMessage(error.message, 'error');
        }
    };

    return (
        <Card>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Create New Quiz</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input id="title" type="text" placeholder="Quiz Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                <Input id="category" type="text" placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} required />
                <Input id="numQ" type="number" placeholder="Number of Questions" value={numQ} onChange={(e) => setNumQ(e.target.value)} required />
                <div className="flex space-x-4 pt-4">
                    <Button type="button" onClick={() => navigateTo('admin_dashboard')} className="bg-gray-500 hover:bg-gray-600">Cancel</Button>
                    <Button type="submit">Create Quiz</Button>
                </div>
            </form>
        </Card>
    );
}
