}

function CreateQuiz({ token, navigateTo, API_BASE_URL, showMessage }) {
    const [category, setCategory] = useState('');
    const [numQ, setNumQ] = useState('');
    const [title, setTitle] = useState('');
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchCategories = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`${API_BASE_URL}/admin/question/categories`, {
                     headers: { 'Authorization': `Bearer ${token}` }
                });
                if(!response.ok) throw new Error('Failed to fetch categories.');
                const data = await response.json();
                setCategories(data);
            } catch (error) {
                showMessage(error.message, 'error');
            } finally {
                setIsLoading(false);
            }
        };
        fetchCategories();
    }, [token, API_BASE_URL, showMessage]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const params = new URLSearchParams({ category, numQ, title });
        try {
            const response = await fetch(`${API_BASE_URL}/admin/quiz/create?${params.toString()}`, {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` },
            });
            if (!response.ok) {
                 const errorText = await response.text();
                 throw new Error(errorText || 'Failed to create quiz.');
            }
            showMessage('Quiz created successfully!', 'success');
            navigateTo('admin_dashboard');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };

    if (isLoading) return <Spinner />;

    return (
        <Card>
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Create New Quiz</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input id="title" placeholder="Quiz Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                <Select id="category" value={category} onChange={(e) => setCategory(e.target.value)} required>
                    <option value="" disabled>Select a Category</option>
                    {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                </Select>
                <Input id="numQ" type="number" placeholder="Number of Questions" value={numQ} onChange={(e) => setNumQ(e.target.value)} required />
                <div className="flex space-x-4 pt-4">
                    <Button type="button" onClick={() => navigateTo('admin_dashboard')} className="bg-gray-500 hover:bg-gray-600">Cancel</Button>
                    <Button type="submit">Create Quiz</Button>
                </div>
            </form>
        </Card>
    );
}
