import React, { useState, useEffect, useCallback } from 'react';

// --- Main App Component ---
export default function App() {
  // State Management
  const [page, setPage] = useState('login');
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);
  const [selectedQuizId, setSelectedQuizId] = useState(null);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [message, setMessage] = useState({ text: '', type: '' });

  // --- API Configuration ---
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8080';


  // --- Helper Functions ---
  const showMessage = useCallback((text, type) => {
    setMessage({ text, type });
    setTimeout(() => setMessage({ text: '', type: '' }), 5000);
  }, []);

  // base64url-safe JWT decode
  const parseJwt = (tkn) => {
    try {
      const base64Url = tkn.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const padded = base64.padEnd(Math.ceil(base64.length / 4) * 4, '=');
      return JSON.parse(atob(padded));
    } catch {
      return null;
    }
  };

  // roles helper
  const extractRoles = (decoded) => {
    if (!decoded) return [];
    if (Array.isArray(decoded.roles)) return decoded.roles;
    if (Array.isArray(decoded.authorities)) return decoded.authorities;
    if (typeof decoded.scope === 'string') return decoded.scope.split(' ');
    return [];
  };

  // centralized fetch with auth + error handling
  const apiFetch = async (path, { method = 'GET', body, headers = {} } = {}) => {
    if (!path.startsWith('http')) path = `${API_BASE_URL}${path}`;
    const init = {
      method,
      headers: {
        Accept: 'application/json',
        ...(body ? { 'Content-Type': 'application/json' } : {}),
        ...(token ? { Authorization: `Bearer ${token}` } : {}),
        ...headers,
      },
      ...(body ? { body: JSON.stringify(body) } : {}),
    };

    const res = await fetch(path, init);
    if (!res.ok) {
      const text = await res.text().catch(() => '');
      const errMsg = text || `Request failed (${res.status})`;
      throw new Error(errMsg);
    }
    // some endpoints may return empty body
    const ct = res.headers.get('content-type') || '';
    return ct.includes('application/json') ? res.json() : null;
  };

  // --- Authentication ---
  useEffect(() => {
    const storedToken = localStorage.getItem('quiz_app_token');
    if (storedToken) {
      const decodedToken = parseJwt(storedToken);
      // if token has exp and is expired -> clear it
      if (decodedToken?.exp && decodedToken.exp * 1000 <= Date.now()) {
        localStorage.removeItem('quiz_app_token');
        return;
      }
      setToken(storedToken);
      const roles = extractRoles(decodedToken);
      setUser({
        username: decodedToken?.sub || decodedToken?.username || 'user',
        roles,
      });
    }
  }, []);

  useEffect(() => {
    if (user) {
      if (user.roles.includes('ROLE_ADMIN')) setPage('admin_dashboard');
      else setPage('user_dashboard');
    } else {
      setPage('login');
    }
  }, [user]);

  const handleLogin = async (username, password) => {
    try {
      const data = await apiFetch('/auth/login', {
        method: 'POST',
        body: { username, password },
      });

      // expect { token: '...' }
      if (!data?.token) throw new Error('No token in login response.');
      // store token first so even if decode fails, we still keep session
      localStorage.setItem('quiz_app_token', data.token);
      setToken(data.token);

      const decoded = parseJwt(data.token);
      const roles = extractRoles(decoded);
      setUser({
        username: decoded?.sub || decoded?.username || username,
        roles,
      });

      showMessage('Login successful!', 'success');
    } catch (error) {
      showMessage(error.message || 'Login failed. Check credentials.', 'error');
    }
  };

  const handleRegister = async (username, password, email) => {
    try {
      await apiFetch('/auth/register', {
        method: 'POST',
        body: { username, password, email },
      });
      showMessage('Registration successful! Please log in.', 'success');
      setPage('login');
    } catch (error) {
      showMessage(error.message || 'Registration failed.', 'error');
    }
  };

  const handleLogout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('quiz_app_token');
    setPage('login');
    showMessage('You have been logged out.', 'success');
  };

  // --- Navigation ---
  const navigateTo = (newPage) => setPage(newPage);
  const startQuiz = (quizId) => {
    setSelectedQuizId(quizId);
    setPage('quiz');
  };
  const editQuestion = (question) => {
    setSelectedQuestion(question);
    setPage('update_question');
  };

  // --- Component Rendering Logic ---
  const renderPage = () => {
    switch (page) {
      case 'register':
        return <Register onRegister={handleRegister} navigateTo={navigateTo} />;
      case 'user_dashboard':
        return (
          <UserDashboard
            token={token}
            startQuiz={startQuiz}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'admin_dashboard':
        return <AdminDashboard navigateTo={navigateTo} />;
      case 'manage_quizzes':
        return (
          <ManageQuizzes
            token={token}
            navigateTo={navigateTo}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'manage_questions':
        return (
          <ManageQuestions
            token={token}
            navigateTo={navigateTo}
            editQuestion={editQuestion}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'quiz':
        return (
          <Quiz
            token={token}
            quizId={selectedQuizId}
            navigateTo={navigateTo}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'add_question':
        return (
          <AddQuestion
            token={token}
            navigateTo={navigateTo}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'update_question':
        return (
          <UpdateQuestion
            token={token}
            question={selectedQuestion}
            navigateTo={navigateTo}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'create_quiz':
        return (
          <CreateQuiz
            token={token}
            navigateTo={navigateTo}
            apiFetch={apiFetch}
            showMessage={showMessage}
          />
        );
      case 'login':
      default:
        return <Login onLogin={handleLogin} navigateTo={navigateTo} />;
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen font-sans">
      <header className="bg-white shadow-md">
        <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
          <div className="text-2xl font-bold text-gray-800">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="inline-block mr-2 text-indigo-600"
            >
              <path d="M14 9a2 2 0 0 1-2 2H6l-4 4V4c0-1.1.9-2 2-2h8a2 2 0 0 1 2 2v5Z" />
              <path d="M18 9h2a2 2 0 0 1 2 2v9l-4-4h-2a2 2 0 0 1-2-2V11a2 2 0 0 1 2-2Z" />
            </svg>
            QuizMaster Pro
          </div>
          {user && (
            <div className="flex items-center space-x-4">
              <span className="text-gray-600">
                Welcome,{' '}
                <span className="font-semibold text-indigo-600">
                  {user.username}
                </span>
              </span>
              <button
                onClick={handleLogout}
                className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
              >
                Logout
              </button>
            </div>
          )}
        </nav>
      </header>
      <main className="container mx-auto p-6">
        {message.text && <Message text={message.text} type={message.type} />}
        {renderPage()}
      </main>
    </div>
  );
}

// --- Reusable UI Components ---
const Card = ({ children, className = '' }) => (
  <div className={`bg-white rounded-xl shadow-lg p-6 md:p-8 ${className}`}>
    {children}
  </div>
);

const Button = ({
  children,
  onClick,
  className = '',
  type = 'button',
  disabled = false,
}) => (
  <button
    type={type}
    onClick={onClick}
    disabled={disabled}
    className={`w-full px-4 py-3 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-300 ${
      disabled ? 'opacity-50 cursor-not-allowed' : ''
    } ${className}`}
  >
    {children}
  </button>
);

const Input = ({
  id,
  type = 'text',
  placeholder,
  value,
  onChange,
  required = false,
}) => (
  <input
    id={id}
    type={type}
    placeholder={placeholder}
    value={value}
    onChange={onChange}
    required={required}
    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
  />
);

const Select = ({
  id,
  value,
  onChange,
  children,
  required = false,
  className = '',
}) => (
  <select
    id={id}
    value={value}
    onChange={onChange}
    required={required}
    className={`w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white ${className}`}
  >
    {children}
  </select>
);

const Message = ({ text, type }) => (
  <div
    className={`p-4 rounded-lg mb-6 text-center ${
      type === 'success'
        ? 'bg-green-100 border-green-400 text-green-700'
        : 'bg-red-100 border-red-400 text-red-700'
    }`}
  >
    <p className="font-bold">{type === 'success' ? 'Success' : 'Error'}</p>
    <p>{text}</p>
  </div>
);

const Spinner = () => (
  <div className="flex justify-center items-center h-64">
    <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-500"></div>
  </div>
);

