// --- User Pages ---
function UserDashboard({ token, startQuiz, apiFetch, showMessage }) {
  const [quizzes, setQuizzes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let abort = false;
    const fetchQuizzes = async () => {
      if (!token) return; // guard
      setIsLoading(true);
      try {
        // NOTE: your backend lists quizzes at /admin/quiz/all
        const data = await apiFetch('/admin/quiz/all');
        if (!abort) setQuizzes(Array.isArray(data) ? data : []);
      } catch (error) {
        if (!abort) showMessage(error.message || 'Failed to fetch quizzes.', 'error');
      } finally {
        if (!abort) setIsLoading(false);
      }
    };
    fetchQuizzes();
    return () => {
      abort = true;
    };
  }, [token, apiFetch, showMessage]);

  if (isLoading) return <Spinner />;
  return (
    <Card>
      <h2 className="text-3xl font-bold mb-6">Available Quizzes</h2>
      {quizzes.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {quizzes.map((quiz) => (
            <div
              key={quiz.id}
              className="bg-gray-50 p-6 rounded-lg border flex flex-col justify-between"
            >
              <div>
                <h3 className="text-xl font-semibold">{quiz.title}</h3>
                <p className="text-gray-500 mt-2">
                  {Array.isArray(quiz.questions) ? quiz.questions.length : 0} Questions
                </p>
              </div>
              <button
                onClick={() => startQuiz(quiz.id)}
                className="mt-4 w-full px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
              >
                Start Quiz
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-500">No quizzes available.</p>
      )}
    </Card>
  );
}

function Quiz({ token, quizId, navigateTo, apiFetch, showMessage }) {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [result, setResult] = useState(null);

  useEffect(() => {
    let abort = false;
    const fetchQuestions = async () => {
      if (!token || !quizId) return; // guard
      setIsLoading(true);
      try {
        const data = await apiFetch(`/user/quiz/get/${quizId}`);
        if (!abort) setQuestions(Array.isArray(data) ? data : []);
      } catch (error) {
        if (!abort) showMessage(error.message || 'Failed to load quiz questions.', 'error');
      } finally {
        if (!abort) setIsLoading(false);
      }
    };
    fetchQuestions();
    return () => {
      abort = true;
    };
  }, [quizId, token, apiFetch, showMessage]);

  const handleSelect = (qId, option) => setAnswers((prev) => ({ ...prev, [qId]: option }));

  const handleSubmit = async () => {
    const responses = questions.map((q) => ({ id: q.id, response: answers[q.id] || '' }));
    try {
      const data = await apiFetch(`/user/quiz/submit/${quizId}`, {
        method: 'POST',
        body: responses,
      });
      // backend may return a number or {score: n}
      const score = typeof data === 'number' ? data : data?.score;
      setResult(score ?? 0);
      showMessage('Quiz submitted!', 'success');
    } catch (error) {
      showMessage(error.message || 'Failed to submit quiz.', 'error');
    }
  };

  if (isLoading) return <Spinner />;
  if (result !== null) {
    return (
      <Card className="text-center">
        <h2 className="text-3xl font-bold">Quiz Complete!</h2>
        <p className="text-xl my-4">Your score is:</p>
        <p className="text-6xl font-bold text-indigo-600">
          {result} / {questions.length}
        </p>
        <Button onClick={() => navigateTo('user_dashboard')} className="mt-8">
          Back to Dashboard
        </Button>
      </Card>
    );
  }

  const q = questions[currentIndex];
  if (!q) return <p>No questions found.</p>;
  const options = [q.option1, q.option2, q.option3, q.option4].filter(Boolean);

  return (
    <Card>
      <p className="text-sm text-gray-500">
        Question {currentIndex + 1} of {questions.length}
      </p>
      <h2 className="text-2xl font-semibold mt-2">{q.question_title}</h2>
      <div className="space-y-4 mt-6">
        {options.map((opt, i) => (
          <button
            key={i}
            onClick={() => handleSelect(q.id, opt)}
            className={`w-full text-left p-4 rounded-lg border-2 ${
              answers[q.id] === opt
                ? 'bg-indigo-100 border-indigo-500'
                : 'bg-white hover:border-indigo-400'
            }`}
          >
            {opt}
          </button>
        ))}
      </div>
      <div className="mt-8 flex justify-between">
        <Button
          onClick={() => setCurrentIndex((i) => i - 1)}
          disabled={currentIndex === 0}
          className="w-auto bg-gray-300 text-gray-700 hover:bg-gray-400"
        >
          Previous
        </Button>
        {currentIndex === questions.length - 1 ? (
          <Button onClick={handleSubmit} className="w-auto bg-green-500 hover:bg-green-600">
            Submit
          </Button>
        ) : (
          <Button onClick={() => setCurrentIndex((i) => i + 1)} className="w-auto">
            Next
          </Button>
        )}
      </div>
    </Card>
  );
}

// --- Admin Pages ---
function AdminDashboard({ navigateTo }) {
  return (
    <Card>
      <h2 className="text-3xl font-bold text-center mb-8">Admin Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div
          onClick={() => navigateTo('manage_quizzes')}
          className="p-8 bg-blue-500 text-white rounded-lg text-center cursor-pointer hover:bg-blue-600 transition-colors"
        >
          <h3 className="text-2xl font-bold">Manage Quizzes</h3>
          <p className="mt-2">View, create, and delete quizzes.</p>
        </div>
        <div
          onClick={() => navigateTo('manage_questions')}
          className="p-8 bg-green-500 text-white rounded-lg text-center cursor-pointer hover:bg-green-600 transition-colors"
        >
          <h3 className="text-2xl font-bold">Manage Questions</h3>
          <p className="mt-2">View, create, update, and delete questions.</p>
        </div>
      </div>
    </Card>
