function ManageQuizzes({ token, navigateTo, apiFetch, showMessage }) {
  const [quizzes, setQuizzes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const fetchQuizzes = useCallback(async () => {
    if (!token) return; // guard
    setIsLoading(true);
    try {
      const data = await apiFetch('/admin/quiz/all');
      setQuizzes(Array.isArray(data) ? data : []);
    } catch (error) {
      showMessage(error.message || 'Failed to fetch quizzes', 'error');
    } finally {
      setIsLoading(false);
    }
  }, [token, apiFetch, showMessage]);

  useEffect(() => {
    let abort = false;
    (async () => {
      await fetchQuizzes();
    })();
    return () => {
      abort = true;
    };
  }, [fetchQuizzes]);

  const deleteQuiz = async (id) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      try {
        await apiFetch(`/admin/quiz/delete/${id}`, { method: 'DELETE' });
        showMessage('Quiz deleted successfully.', 'success');
        fetchQuizzes(); // Refresh the list
      } catch (error) {
        showMessage(error.message || 'Failed to delete quiz.', 'error');
      }
    }
  };

  if (isLoading) return <Spinner />;

  return (
    <Card>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold">Manage Quizzes ({quizzes.length})</h2>
        <div className="flex space-x-4">
          <Button
            onClick={() => navigateTo('create_quiz')}
            className="w-auto bg-green-500 hover:bg-green-600"
          >
            Create Quiz
          </Button>
          <Button
            onClick={() => navigateTo('admin_dashboard')}
            className="w-auto bg-gray-500 hover:bg-gray-600"
          >
            Back
          </Button>
        </div>
      </div>
      <div className="max-h-[60vh] overflow-y-auto space-y-3 pr-2">
        {quizzes.length > 0 ? (
          quizzes.map((quiz) => (
            <div
              key={quiz.id}
              className="bg-gray-50 p-4 rounded-lg flex justify-between items-center border"
            >
              <div>
                <p className="font-semibold">{quiz.title}</p>
                <p className="text-sm text-gray-500">
                  {Array.isArray(quiz.questions) ? quiz.questions.length : 0} questions
                </p>
              </div>
              <button
                onClick={() => deleteQuiz(quiz.id)}
                className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No quizzes created yet.</p>
        )}
      </div>
    </Card>
  );
}

function ManageQuestions({ token, navigateTo, editQuestion, apiFetch, showMessage }) {
  const [questions, setQuestions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [filter, setFilter] = useState('All');

  const fetchData = useCallback(async () => {
    if (!token) return; // guard
    setIsLoading(true);
    try {
      const [qs, cats] = await Promise.all([
        apiFetch('/admin/question/allQuestions'),
        apiFetch('/admin/question/categories'),
      ]);
      setQuestions(Array.isArray(qs) ? qs : []);
      setCategories(['All', ...(Array.isArray(cats) ? cats : [])]);
    } catch (error) {
      showMessage(error.message || 'Failed to fetch data', 'error');
    } finally {
      setIsLoading(false);
    }
  }, [token, apiFetch, showMessage]);

  useEffect(() => {
    let abort = false;
    (async () => {
      await fetchData();
    })();
    return () => {
      abort = true;
    };
  }, [fetchData]);

  const deleteQuestion = async (id) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      try {
        await apiFetch(`/admin/question/delete/${id}`, { method: 'DELETE' });
        showMessage('Question deleted successfully.', 'success');
        fetchData();
      } catch (error) {
        showMessage(error.message || 'Failed to delete question.', 'error');
      }
    }
  };

  const filteredQuestions =
    filter === 'All' ? questions : questions.filter((q) => q.category === filter);

  if (isLoading) return <Spinner />;
  return (
    <Card>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold">Manage Questions ({filteredQuestions.length})</h2>
        <div className="flex space-x-4">
          <Button
            onClick={() => navigateTo('add_question')}
            className="w-auto bg-blue-500 hover:bg-blue-600"
          >
            Add Question
          </Button>
          <Button
            onClick={() => navigateTo('admin_dashboard')}
            className="w-auto bg-gray-500 hover:bg-gray-600"
          >
            Back
          </Button>
        </div>
      </div>
      <div className="flex justify-end items-center mb-4">
        <label htmlFor="category-filter" className="mr-2">
          Filter by Category:
        </label>
        <Select
          id="category-filter"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="w-48"
        >
          {categories.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </Select>
      </div>
      <div className="max-h-[60vh] overflow-y-auto space-y-3 pr-2">
        {filteredQuestions.length > 0 ? (
          filteredQuestions.map((q) => (
            <div key={q.id} className="bg-gray-50 p-3 rounded-lg border">
              <p className="font-semibold">{q.question_title}</p>
              <p className="text-sm text-gray-500">Category: {q.category}</p>
              <div className="mt-2 flex space-x-2">
                <button
                  onClick={() => editQuestion(q)}
                  className="px-3 py-1 bg-yellow-500 text-white text-sm rounded hover:bg-yellow-600"
                >
                  Update
                </button>
                <button
                  onClick={() => deleteQuestion(q.id)}
                  className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No questions found for this category.</p>
        )}
      </div>
    </Card>
  );
}

function AddQuestion({ token, navigateTo, apiFetch, showMessage }) {
  const [formData, setFormData] = useState({
    question_title: '',
    option1: '',
    option2: '',
    option3: '',
    option4: '',
    right_answer: '',
    difficultylevel: '',
    category: '',
  });
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.id]: e.target.value });
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await apiFetch('/admin/question/addQuestions', {
        method: 'POST',
        body: formData,
      });
      showMessage('Question added!', 'success');
      navigateTo('manage_questions');
    } catch (error) {
      showMessage(error.message || 'Failed to add question.', 'error');
    }
  };
  return (
    <Card>
      <h2 className="text-3xl font-bold mb-6">Add New Question</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <Input
          id="question_title"
          placeholder="Question Title"
          value={formData.question_title}
          onChange={handleChange}
