import React, { useState, useEffect, useCallback } from 'react';

// --- Main App Component ---
export default function App() {
    // State Management
    const [page, setPage] = useState('login');
    const [token, setToken] = useState(null);
    const [user, setUser] = useState(null);
    const [selectedQuizId, setSelectedQuizId] = useState(null);
    const [selectedQuestion, setSelectedQuestion] = useState(null);
    const [message, setMessage] = useState({ text: '', type: '' });

    // --- API Configuration ---
    const API_BASE_URL = 'http://localhost:8080';

    // --- Helper Functions ---
    const showMessage = useCallback((text, type) => {
        setMessage({ text, type });
        setTimeout(() => setMessage({ text: '', type: '' }), 5000);
    }, []);

    const parseJwt = (token) => {
        try {
            return JSON.parse(atob(token.split('.')[1]));
        } catch (e) {
            return null;
        }
    };

    // --- Authentication ---
    useEffect(() => {
        const storedToken = localStorage.getItem('quiz_app_token');
        if (storedToken) {
            const decodedToken = parseJwt(storedToken);
            if (decodedToken && decodedToken.exp * 1000 > Date.now()) {
                setToken(storedToken);
                setUser({
                    username: decodedToken.sub,
                    roles: decodedToken.roles || []
                });
            } else {
                localStorage.removeItem('quiz_app_token');
            }
        }
    }, []);

    useEffect(() => {
        if (user) {
            if (user.roles.includes('ROLE_ADMIN')) {
                setPage('admin_dashboard');
            } else {
                setPage('user_dashboard');
            }
        } else {
            setPage('login');
        }
    }, [user]);

    const handleLogin = async (username, password) => {
        try {
            const response = await fetch(`${API_BASE_URL}/auth/login`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });
            if (!response.ok) throw new Error('Login failed. Check credentials.');
            const data = await response.json();
            const decodedToken = parseJwt(data.token);
            localStorage.setItem('quiz_app_token', data.token);
            setToken(data.token);
            setUser({
                username: decodedToken.sub,
                roles: decodedToken.roles || []
            });
            showMessage('Login successful!', 'success');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };

    const handleRegister = async (username, password, email) => {
        try {
            const response = await fetch(`${API_BASE_URL}/auth/register`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password, email }),
            });
            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText || 'Registration failed.');
            }
            showMessage('Registration successful! Please log in.', 'success');
            setPage('login');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };

    const handleLogout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('quiz_app_token');
        setPage('login');
        showMessage('You have been logged out.', 'success');
    };

    // --- Navigation ---
    const navigateTo = (newPage) => setPage(newPage);
    const startQuiz = (quizId) => {
        setSelectedQuizId(quizId);
        setPage('quiz');
    };
    const editQuestion = (question) => {
        setSelectedQuestion(question);
        setPage('update_question');
    };

    // --- Component Rendering Logic ---
    const renderPage = () => {
        switch (page) {
            case 'register':
                return <Register onRegister={handleRegister} navigateTo={navigateTo} />;
            case 'user_dashboard':
                return <UserDashboard token={token} startQuiz={startQuiz} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'admin_dashboard':
                return <AdminDashboard token={token} navigateTo={navigateTo} editQuestion={editQuestion} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'quiz':
                return <Quiz token={token} quizId={selectedQuizId} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'add_question':
                return <AddQuestion token={token} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'update_question':
                return <UpdateQuestion token={token} question={selectedQuestion} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'create_quiz':
                return <CreateQuiz token={token} navigateTo={navigateTo} API_BASE_URL={API_BASE_URL} showMessage={showMessage} />;
            case 'login':
            default:
                return <Login onLogin={handleLogin} navigateTo={navigateTo} />;
        }
    };

    return (
        <div className="bg-gray-100 min-h-screen font-sans">
            <header className="bg-white shadow-md">
                <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
                    <div className="text-2xl font-bold text-gray-800">
                        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="inline-block mr-2 text-indigo-600"><path d="M14 9a2 2 0 0 1-2 2H6l-4 4V4c0-1.1.9-2 2-2h8a2 2 0 0 1 2 2v5Z"/><path d="M18 9h2a2 2 0 0 1 2 2v9l-4-4h-2a2 2 0 0 1-2-2V11a2 2 0 0 1 2-2Z"/></svg>
                        QuizMaster Pro
                    </div>
                    {user && (
                        <div className="flex items-center space-x-4">
                            <span className="text-gray-600">Welcome, <span className="font-semibold text-indigo-600">{user.username}</span></span>
                            <button onClick={handleLogout} className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600">Logout</button>
                        </div>
                    )}
                </nav>
            </header>
            <main className="container mx-auto p-6">
                {message.text && <Message text={message.text} type={message.type} />}
                {renderPage()}
            </main>
        </div>
    );
}

// --- Reusable UI Components ---
const Card = ({ children, className = '' }) => <div className={`bg-white rounded-xl shadow-lg p-6 md:p-8 ${className}`}>{children}</div>;
const Button = ({ children, onClick, className = '', type = 'button' }) => <button type={type} onClick={onClick} className={`w-full px-4 py-3 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-300 ${className}`}>{children}</button>;
const Input = ({ id, type, placeholder, value, onChange, required = false }) => <input id={id} type={type} placeholder={placeholder} value={value} onChange={onChange} required={required} className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" />;
const Select = ({ id, value, onChange, children, required = false }) => <select id={id} value={value} onChange={onChange} required={required} className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white">{children}</select>;
const Message = ({ text, type }) => <div className={`p-4 rounded-lg mb-6 text-center ${type === 'success' ? 'bg-green-100 border-green-400 text-green-700' : 'bg-red-100 border-red-400 text-red-700'}`}><p className="font-bold">{type === 'success' ? 'Success' : 'Error'}</p><p>{text}</p></div>;
const Spinner = () => <div className="flex justify-center items-center h-64"><div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-500"></div></div>;

// --- Authentication Pages ---
function Login({ onLogin, navigateTo }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const handleSubmit = (e) => { e.preventDefault(); onLogin(username, password); };
    return (
        <div className="max-w-md mx-auto mt-10">
            <Card>
                <h2 className="text-3xl font-bold text-center mb-6">Login</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div><label htmlFor="username">Username</label><Input id="username" type="text" value={username} onChange={(e) => setUsername(e.target.value)} required /></div>
                    <div><label htmlFor="password">Password</label><Input id="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required /></div>
                    <Button type="submit">Login</Button>
                </form>
                <p className="text-center mt-6">Don't have an account? <button onClick={() => navigateTo('register')} className="font-semibold text-indigo-600 hover:text-indigo-500">Register</button></p>
            </Card>
        </div>
    );
}

function Register({ onRegister, navigateTo }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const handleSubmit = (e) => { e.preventDefault(); onRegister(username, password, email); };
    return (
        <div className="max-w-md mx-auto mt-10">
            <Card>
                <h2 className="text-3xl font-bold text-center mb-6">Create Account</h2>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div><label htmlFor="username-reg">Username</label><Input id="username-reg" type="text" value={username} onChange={(e) => setUsername(e.target.value)} required /></div>
                    <div><label htmlFor="email-reg">Email (Optional)</label><Input id="email-reg" type="email" value={email} onChange={(e) => setEmail(e.target.value)} /></div>
                    <div><label htmlFor="password-reg">Password</label><Input id="password-reg" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required /></div>
                    <Button type="submit">Register</Button>
                </form>
                <p className="text-center mt-6">Already have an account? <button onClick={() => navigateTo('login')} className="font-semibold text-indigo-600 hover:text-indigo-500">Login</button></p>
            </Card>
        </div>
    );
}

// --- User Pages ---
function UserDashboard({ token, startQuiz, API_BASE_URL, showMessage }) {
    const [quizzes, setQuizzes] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchQuizzes = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/admin/quiz/all`, { headers: { 'Authorization': `Bearer ${token}` } });
                if (!response.ok) throw new Error('Failed to fetch quizzes.');
                const data = await response.json();
                setQuizzes(data);
            } catch (error) {
                showMessage(error.message, 'error');
            } finally {
                setIsLoading(false);
            }
        };
        fetchQuizzes();
    }, [token, API_BASE_URL, showMessage]);

    if (isLoading) return <Spinner />;
    return (
        <Card>
            <h2 className="text-3xl font-bold mb-6">Available Quizzes</h2>
            {quizzes.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {quizzes.map(quiz => (
                        <div key={quiz.id} className="bg-gray-50 p-6 rounded-lg border flex flex-col justify-between">
                            <div>
                                <h3 className="text-xl font-semibold">{quiz.title}</h3>
                                <p className="text-gray-500 mt-2">{quiz.questions.length} Questions</p>
                            </div>
                            <button onClick={() => startQuiz(quiz.id)} className="mt-4 w-full px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600">Start Quiz</button>
                        </div>
                    ))}
                </div>
            ) : <p className="text-center text-gray-500">No quizzes available.</p>}
        </Card>
    );
}

function Quiz({ token, quizId, navigateTo, API_BASE_URL, showMessage }) {
    const [questions, setQuestions] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [answers, setAnswers] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [result, setResult] = useState(null);

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/user/quiz/get/${quizId}`, { headers: { 'Authorization': `Bearer ${token}` } });
                if (!response.ok) throw new Error('Failed to load quiz questions.');
                setQuestions(await response.json());
            } catch (error) {
                showMessage(error.message, 'error');
            } finally {
                setIsLoading(false);
            }
        };
        fetchQuestions();
    }, [quizId, token, API_BASE_URL, showMessage]);

    const handleSelect = (qId, option) => setAnswers({ ...answers, [qId]: option });
    const handleSubmit = async () => {
        const responses = questions.map(q => ({ id: q.id, response: answers[q.id] || '' }));
        try {
            const response = await fetch(`${API_BASE_URL}/user/quiz/submit/${quizId}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
                body: JSON.stringify(responses)
            });
            if (!response.ok) throw new Error('Failed to submit quiz.');
            setResult(await response.json());
            showMessage('Quiz submitted!', 'success');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };

    if (isLoading) return <Spinner />;
    if (result !== null) {
        return (
            <Card className="text-center">
                <h2 className="text-3xl font-bold">Quiz Complete!</h2>
                <p className="text-xl my-4">Your score is:</p>
                <p className="text-6xl font-bold text-indigo-600">{result} / {questions.length}</p>
                <Button onClick={() => navigateTo('user_dashboard')} className="mt-8">Back to Dashboard</Button>
            </Card>
        );
    }

    const q = questions[currentIndex];
    if (!q) return <p>No questions found.</p>;
    const options = [q.option1, q.option2, q.option3, q.option4];

    return (
        <Card>
            <p className="text-sm text-gray-500">Question {currentIndex + 1} of {questions.length}</p>
            <h2 className="text-2xl font-semibold mt-2">{q.question_title}</h2>
            <div className="space-y-4 mt-6">
                {options.map((opt, i) => <button key={i} onClick={() => handleSelect(q.id, opt)} className={`w-full text-left p-4 rounded-lg border-2 ${answers[q.id] === opt ? 'bg-indigo-100 border-indigo-500' : 'bg-white hover:border-indigo-400'}`}>{opt}</button>)}
            </div>
            <div className="mt-8 flex justify-between">
                <Button onClick={() => setCurrentIndex(i => i - 1)} disabled={currentIndex === 0} className="w-auto bg-gray-300 text-gray-700 hover:bg-gray-400 disabled:opacity-50">Previous</Button>
                {currentIndex === questions.length - 1
                    ? <Button onClick={handleSubmit} className="w-auto bg-green-500 hover:bg-green-600">Submit</Button>
                    : <Button onClick={() => setCurrentIndex(i => i + 1)} className="w-auto">Next</Button>}
            </div>
        </Card>
    );
}

// --- Admin Pages ---
function AdminDashboard({ token, navigateTo, editQuestion, API_BASE_URL, showMessage }) {
    const [questions, setQuestions] = useState([]);
    const [quizzes, setQuizzes] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [filter, setFilter] = useState('All');
    const [categories, setCategories] = useState([]);

    const fetchData = useCallback(async () => {
        setIsLoading(true);
        // FIX: Fetch data in separate try/catch blocks for robustness
        try {
            const qRes = await fetch(`${API_BASE_URL}/admin/question/allQuestions`, { headers: { 'Authorization': `Bearer ${token}` } });
            if (!qRes.ok) throw new Error(`Failed to fetch questions (Status: ${qRes.status})`);
            setQuestions(await qRes.json());
        } catch (error) {
            showMessage(error.message, 'error');
            setQuestions([]); // Clear data on error
        }

        try {
            const quizRes = await fetch(`${API_BASE_URL}/admin/quiz/all`, { headers: { 'Authorization': `Bearer ${token}` } });
            if (!quizRes.ok) throw new Error(`Failed to fetch quizzes (Status: ${quizRes.status})`);
            setQuizzes(await quizRes.json());
        } catch (error) {
            showMessage(error.message, 'error');
            setQuizzes([]); // Clear data on error
        }

        try {
            const catRes = await fetch(`${API_BASE_URL}/admin/question/categories`, { headers: { 'Authorization': `Bearer ${token}` } });
            if (!catRes.ok) throw new Error(`Failed to fetch categories (Status: ${catRes.status})`);
            setCategories(['All', ...await catRes.json()]);
        } catch (error) {
            showMessage(error.message, 'error');
            setCategories(['All']); // Set default on error
        }

        setIsLoading(false);
    }, [token, API_BASE_URL, showMessage]);


    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const deleteItem = async (type, id) => {
        const endpoint = type === 'quiz' ? `${API_BASE_URL}/admin/quiz/delete/${id}` : `${API_BASE_URL}/admin/question/delete/${id}`;
        if (window.confirm(`Are you sure you want to delete this ${type}?`)) {
            try {
                const response = await fetch(endpoint, {
                    method: 'DELETE',
                    headers: { 'Authorization': `Bearer ${token}` }
                });
                if (!response.ok) {
                     const errorText = await response.text();
                     throw new Error(errorText || `Failed to delete ${type}.`);
                }
                showMessage(`${type.charAt(0).toUpperCase() + type.slice(1)} deleted successfully.`, 'success');
                fetchData(); // Refresh data on success
            } catch (error) {
                showMessage(error.message, 'error');
            }
        }
    };

    const filteredQuestions = filter === 'All' ? questions : questions.filter(q => q.category === filter);

    if (isLoading) return <Spinner />;
    return (
        <div className="space-y-8">
            <Card>
                <h2 className="text-3xl font-bold mb-6">Admin Dashboard</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <Button onClick={() => navigateTo('add_question')} className="bg-blue-500 hover:bg-blue-600">Add New Question</Button>
                    <Button onClick={() => navigateTo('create_quiz')} className="bg-green-500 hover:bg-green-600">Create New Quiz</Button>
                </div>
            </Card>

            <Card>
                <h3 className="text-2xl font-bold mb-4">Manage Quizzes ({quizzes.length})</h3>
                <div className="max-h-96 overflow-y-auto space-y-3 pr-2">
                    {quizzes.length > 0 ? quizzes.map(quiz => (
                        <div key={quiz.id} className="bg-gray-50 p-4 rounded-lg flex justify-between items-center border">
                            <div><p className="font-semibold">{quiz.title}</p><p className="text-sm text-gray-500">{quiz.questions.length} questions</p></div>
                            <button onClick={() => deleteItem('quiz', quiz.id)} className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600">Delete</button>
                        </div>
                    )) : <p className="text-gray-500">No quizzes created.</p>}
                </div>
            </Card>

            <Card>
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-2xl font-bold">Manage Questions ({filteredQuestions.length})</h3>
                    <Select value={filter} onChange={(e) => setFilter(e.target.value)} className="w-48">
                        {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                    </Select>
                </div>
                <div className="max-h-96 overflow-y-auto space-y-3 pr-2">
                    {filteredQuestions.length > 0 ? filteredQuestions.map(q => (
                        <div key={q.id} className="bg-gray-50 p-3 rounded-lg border">
                            <p className="font-semibold">{q.question_title}</p>
                            <p className="text-sm text-gray-500">Category: {q.category}</p>
                            <div className="mt-2 flex space-x-2">
                                <button onClick={() => editQuestion(q)} className="px-3 py-1 bg-yellow-500 text-white text-sm rounded hover:bg-yellow-600">Update</button>
                                <button onClick={() => deleteItem('question', q.id)} className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600">Delete</button>
                            </div>
                        </div>
                    )) : <p className="text-gray-500">No questions found for this category.</p>}
                </div>
            </Card>
        </div>
    );
}

function AddQuestion({ token, navigateTo, API_BASE_URL, showMessage }) {
    const [formData, setFormData] = useState({ question_title: '', option1: '', option2: '', option3: '', option4: '', right_answer: '', difficultylevel: '', category: '' });
    const handleChange = (e) => setFormData({ ...formData, [e.target.id]: e.target.value });
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${API_BASE_URL}/admin/question/addQuestions`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
                body: JSON.stringify(formData)
            });
            if (!response.ok) throw new Error('Failed to add question.');
            showMessage('Question added!', 'success');
            navigateTo('admin_dashboard');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };
    return (
        <Card>
            <h2 className="text-3xl font-bold mb-6">Add New Question</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input id="question_title" placeholder="Question Title" value={formData.question_title} onChange={handleChange} required />
                <Input id="option1" placeholder="Option 1" value={formData.option1} onChange={handleChange} required />
                <Input id="option2" placeholder="Option 2" value={formData.option2} onChange={handleChange} required />
                <Input id="option3" placeholder="Option 3" value={formData.option3} onChange={handleChange} required />
                <Input id="option4" placeholder="Option 4" value={formData.option4} onChange={handleChange} required />
                <Input id="right_answer" placeholder="Right Answer" value={formData.right_answer} onChange={handleChange} required />
                <Input id="difficultylevel" placeholder="Difficulty Level" value={formData.difficultylevel} onChange={handleChange} required />
                <Input id="category" placeholder="Category" value={formData.category} onChange={handleChange} required />
                <div className="flex space-x-4 pt-4">
                    <Button type="button" onClick={() => navigateTo('admin_dashboard')} className="bg-gray-500 hover:bg-gray-600">Cancel</Button>
                    <Button type="submit">Add Question</Button>
                </div>
            </form>
        </Card>
    );
}

function UpdateQuestion({ token, question, navigateTo, API_BASE_URL, showMessage }) {
    const [formData, setFormData] = useState(question);
    const handleChange = (e) => setFormData({ ...formData, [e.target.id]: e.target.value });
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${API_BASE_URL}/admin/question/update/${question.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
                body: JSON.stringify(formData)
            });
            if (!response.ok) throw new Error('Failed to update question.');
            showMessage('Question updated!', 'success');
            navigateTo('admin_dashboard');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };
    return (
        <Card>
            <h2 className="text-3xl font-bold mb-6">Update Question</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input id="question_title" placeholder="Question Title" value={formData.question_title} onChange={handleChange} required />
                <Input id="option1" placeholder="Option 1" value={formData.option1} onChange={handleChange} required />
                <Input id="option2" placeholder="Option 2" value={formData.option2} onChange={handleChange} required />
                <Input id="option3" placeholder="Option 3" value={formData.option3} onChange={handleChange} required />
                <Input id="option4" placeholder="Option 4" value={formData.option4} onChange={handleChange} required />
                <Input id="right_answer" placeholder="Right Answer" value={formData.right_answer} onChange={handleChange} required />
                <Input id="difficultylevel" placeholder="Difficulty Level" value={formData.difficultylevel} onChange={handleChange} required />
                <Input id="category" placeholder="Category" value={formData.category} onChange={handleChange} required />
                <div className="flex space-x-4 pt-4">
                    <Button type="button" onClick={() => navigateTo('admin_dashboard')} className="bg-gray-500 hover:bg-gray-600">Cancel</Button>
                    <Button type="submit">Update Question</Button>
                </div>
            </form>
        </Card>
    );
}

function CreateQuiz({ token, navigateTo, API_BASE_URL, showMessage }) {
    const [category, setCategory] = useState('');
    const [numQ, setNumQ] = useState('');
    const [title, setTitle] = useState('');
    const [categories, setCategories] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await fetch(`${API_BASE_URL}/admin/question/categories`, { headers: { 'Authorization': `Bearer ${token}` } });
                if (!response.ok) throw new Error('Failed to fetch categories.');
                const data = await response.json();
                setCategories(data);
            } catch (error) {
                showMessage(error.message, 'error');
            } finally {
                setIsLoading(false);
            }
        };
        fetchCategories();
    }, [token, API_BASE_URL, showMessage]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const params = new URLSearchParams({ category, numQ, title });
        try {
            const response = await fetch(`${API_BASE_URL}/admin/quiz/create?${params.toString()}`, {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` },
            });
            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText || 'Failed to create quiz.');
            }
            showMessage('Quiz created successfully!', 'success');
            navigateTo('admin_dashboard');
        } catch (error) {
            showMessage(error.message, 'error');
        }
    };

    if (isLoading) return <Spinner />;
    return (
        <Card>
            <h2 className="text-3xl font-bold mb-6">Create New Quiz</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input id="title" placeholder="Quiz Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                <Select id="category" value={category} onChange={(e) => setCategory(e.target.value)} required>
                    <option value="" disabled>Select a Category</option>
                    {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                </Select>
                <Input id="numQ" type="number" placeholder="Number of Questions" value={numQ} onChange={(e) => setNumQ(e.target.value)} required />
                <div className="flex space-x-4 pt-4">
                    <Button type="button" onClick={() => navigateTo('admin_dashboard')} className="bg-gray-500 hover:bg-gray-600">Cancel</Button>
                    <Button type="submit">Create Quiz</Button>
                </div>
            </form>
        </Card>
    );
}
